import { useEffect, useState } from 'react';
import Header from '../Header';
import axios from 'axios';
import { useNavigate } from 'react-router';

function Form() {
    const [pessoas, setPessoas] = useState([]);
    const navegate = useNavigate([]);
    function handleFormSubmit(event){
        event.preventDefault();
        axios.post('http://localhost:3001/cadastro', campos).then(response => {
            carregarPessoa();
            alert( ' cadastro realizado com sucesso!');
        })
       //console.log(campos);
    }



    const[estados, setEstados] = useState([]);
    useEffect(() =>{ 
        axios.get('http://servicodados.ibge.gov.br/api/v1/localidades/estados')
            .then(response=>{
                setEstados(response.data);
            })
    },[]);
    const [campos, setCampos] = useState({
        txtNome: '',
        txtIdade: 0,
        cmbUF: '0'
    })

    function handleInputChange(event) {
        campos[event.target.name] = event.target.value;
        setCampos(campos);
    }
    const carregarPessoa = async () => {
        await axios.get('http://localhost:3001/listar').then((response)=> {
           const {data} = response;        
           setPessoas(data);
        })
    }

    const removerPessoa = async (e, id) => {
        e.preventDefault() 

        await axios.delete("http://localhost:3001/pessoa/" +id).then((response) => {
            console.log(response)
            carregarPessoa();
        })  
    }

    const alterarPessoa = async (e, id) => {
        e.preventDefault() 

        await axios.get("http://localhost:3001/pessoa/" + id).then((response) => {
            console.log(response)
            carregarPessoa();
        })  
    }


    useEffect(()=> {
       carregarPessoa()
       console.log(pessoas)
    }, [])
    return (
        <div>
           <Header title="React Form" />
           <form onSubmit={handleFormSubmit}>
            <fieldset>
                <legend>
                    <h2>Dados de Cadastro</h2>
                </legend>

                <div>
                    <label>Nome:
                        <input type="text" name="txtNome" id="txtNome"  onChange={handleInputChange}/>

                    </label>
                </div>

                <div>
                    <label>Idade:
                        <input type="number" name="txtIdade" id="txtIdade" onChange={handleInputChange}/>

                    </label>
                </div>

                <div>
                    <label>UF:
                        <select name="cmbUF" onChange={handleInputChange} >
                            <option value="0">Selecione uma opção</option>
                            {estados.map(estado => (<option key={estado.sigla} value={estado.sigla}>{estado.sigla}</option>))}

                        </select>
                    </label>
                </div>
                <input type="submit" value="Salvar" />
            </fieldset>
           </form>

        {/*<Link to="/"> Acessar cadastro </Link>*/} 

        <table> 
            <thead>
                <tr>
                    <td>id</td>
                    <td>nome</td>
                    <td>idade</td>
                    <td>uf</td>
                </tr>
            </thead>
            <tbody>
        {
            pessoas.map((item)=> {
                return (
                    <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>{item.idade}</td>
                        <td>{item.uf}</td>
                        <td><button onClick={(e) =>removerPessoa(e,item.id)}>Deletar</button></td>
                        <td><button onClick={(e) =>alterarPessoa(e,item.id)}>Editar</button></td>
                    </tr>
                )
            })
        }
        </tbody>
        </table>
        </div>

    )
}

export default Form;
